mapper.select:
SELECT POST_ID, POST_TITLE, POST_CONTENT, POST_AUTHOR, POST_DEL_YN, CREATE_DATE, UPDATE_DATE FROM TD_POST WHERE POST_DEL_YN = 'N'
AND POST_TITLE LIKE CONCAT('%', #{keyword}, '%')
ORDER BY CREATE_DATE DESC LIMIT #{limit} OFFSET #{offset}

mapper.update:
UPDATE TD_POST
SET
    POST_TITLE   = #{postTitle},
    POST_CONTENT = #{postContent}
WHERE POST_ID = #{postId}
    AND POST_DEL_YN = 'N'
